    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
JOptionPane.showMessageDialog(null,"Think of any real number");
JOptionPane.showMessageDialog(null,"Now multiply it with 2");
JOptionPane.showMessageDialog(null,"Add 10");
JOptionPane.showMessageDialog(null,"Multiply it by 20");
JOptionPane.showMessageDialog(null,"Subtract 5");
JOptionPane.showMessageDialog(null,"Divide by 10");
JOptionPane.showMessageDialog(null,"Add 8");
Double a=Double.parseDouble(JOptionPane.showInputDialog("Enter the result in the box below :-"));
System.out.println("The result entered is : "+a);
Double y=(((((a-8)*10)+5)/20)-10)/2;
JOptionPane.showMessageDialog(null, "The number you thought about is : "+y);
    }//GEN-LAST:event_jButton1ActionPerformed

