    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
Double coe=1.602192*Math.pow(10, -19);
Double ctmr=1.76*Math.pow(10, 11);
Double moe=coe/ctmr;
Double eV=1.602*Math.pow(10, -19);
Double h=6.63*Math.pow(10, -34);
System.out.println("e = "+coe+" C");
System.out.println("e/m = "+ctmr+" C/kg");
System.out.println("mass of electron [e/(e/m)] = "+moe+" kg");
System.out.println("c = 299,792,458 m/s");
System.out.println("Planck's Constant, h = "+h);
System.out.println("1 eV = "+eV+" J");
System.out.println("x °C = x + 273 K");// TODO add your handling code here:
    }//GEN-LAST:event_formWindowOpened

