    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
String a=new String(jTextField1.getText());
String b=new String(jTextField2.getText());
Double c=Double.parseDouble(jTextField3.getText());
Double d=Double.parseDouble(jTextField4.getText());
Double e=Double.parseDouble(jTextField5.getText());
Double f=Double.parseDouble(jTextField6.getText());
Double g=Double.parseDouble(jTextField7.getText());
Double x=c+d+e+f+g;
jLabel9.setText(""+x);
Double y=x/500*100;
jLabel11.setText(""+y);
  if(y>100.0)
{
    jLabel14.setText(""+b+", please enter the marks correctly");
    jLabel11.setText("Error");
    jLabel9.setText("Error");
}
else
  if (y>=85.0)
    jLabel14.setText("Congrats, u got distinction, "+b);
else
  if(y>=75.0)
    jLabel14.setText("Congrats, u got "+y+"% marks. Keep it up "+b);
else
    if (y>=40.0)
    jLabel14.setText("U passed with "+y+"% marks "+b+". Try to do better next time.");
else
    if(y<0.0)
{
    jLabel14.setText(""+b+", please enter the marks correctly");
    jLabel11.setText("Error");
    jLabel9.setText("Error");
}
else
  if (y<40.0)
    jLabel14.setText("U failed! No biggie, try again "+b);
// TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

