    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
String a=new String(jTextField1.getText());
String b=new String(jTextField2.getText());
Double c=Double.parseDouble(jTextField3.getText());
Double d=Double.parseDouble(jTextField4.getText());
Double e=Double.parseDouble(jTextField5.getText());
Double f=Double.parseDouble(jTextField6.getText());
Double g=Double.parseDouble(jTextField7.getText());
Double x=c+d+e+f+g;
jLabel9.setText(""+x);
Double y=x/500*100;
jLabel11.setText(""+y);
if (y>=(85.0))
    jLabel12.setText("Congrats u passed with distinction  "+b);
else
    jLabel12.setText("Work a bit harder");        // TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

