    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
//to obtain
        String txt=jTextField1.getText();
        double sales, comm;
        sales=Double.parseDouble(txt);
        //calculate commission
        if (sales>=50000)
            comm=sales*0.25;
        else
            if (sales>=35000)
            comm=sales*0.20;
            else
                if (sales>=20000)
            comm=sales*0.15;
                else
                    if (sales>=10000)
            comm=sales*0.10;
                    else
                        comm=0;
        //display output
        jLabel1.setText("The commission is Rs "+comm);// TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

