    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
jTextArea1.setText(null);
int n=Integer.parseInt(jTextField1.getText());
long first, second, third;        // TODO add your handling code here:
first=0;
second=1;
for(int i=2;i<n;++i)
{
    third=first+second;
    System.out.println(third);
    first=second;
    second=third;
jTextArea1.setText(null);
jTextArea1.append("Fibonacci Series :-");
jTextArea1.append(""+first+second+third);
}
String ask=JOptionPane.showInputDialog("Do you want to continue? (y/n)");
if(ask.equals("y")||ask.equals("y")){
    jTextField1.setText(null);
    jTextArea1.setText(null);
        }
 else if(ask.equals("n")||ask.equals("N")){
     System.exit(0);
 }
    }//GEN-LAST:event_jButton2ActionPerformed

